using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;public class GPoints : MonoBehaviour{    Score myscore;    public void Start()    {        myscore = GameObject.Find("ScoreController").GetComponent<Score>();    }    void OnCollisionEnter(Collision collision)    {        if (collision.gameObject.tag == "Quaffle")        {            myscore.UpdateGscore();            Destroy(collision.gameObject);        }    }}

